import React,{Component} from 'react';
import { StyleSheet, Text, View, FlatList } from 'react-native';
import {db} from "../firebase/config"
import Fotos from '../components/paginafotos';


class Home extends Component{
  constructor(){
    super()
    this.state={
        post : []

    }}
    componentDidMount(){
      db.collection("posts").onSnapshot((docs)=>{
        let posts = []
        docs.forEach((doc)=>{
            posts.push({
                id: doc.id,
                data: doc.data()
            })
        })
        this.setState({
            post: posts
        })
    })
    }
    render(){
  return (
    <View style={styles.container}>
      <Text>Pagina de Home!</Text>
      <FlatList  
                    data={this.state.post} // tiene que tener la info que le vamso a dar
                    keyExtractor={(data)=> data.id}
                    renderItem={(item)=>( <Posts data={item}/> )}  >
                </FlatList>
                {/* no existe un map, flatlist es lo mismo aprox */}


    </View>
  );
    }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});

export default Home
