import React,{Component} from 'react';
import { StyleSheet, Text, View, TouchableOpacity } from 'react-native';
import { db, auth } from '../firebase/config';
import firebase from 'firebase';
import "firebase/firestore"


class Fotos extends Component{
    constructor(props){
      super(props)
      this.state={
      likeado: false
      }
    }

like(){
    if (this.state.liked == false) {
        var agregarLike = db.collection("posteos").doc(`${item.id}`);

        return agregarLike.update({
            likes : firebase.firestore.FieldValue.arrayUnion(`${auth.currentUser.email}`)
        })
        .then(() => {
            this.setState({
            /*  likes: this.state.likes+=1, */
                liked: true
            })
        })
        .catch((error) => {
            console.error("Error updating document: ", error);
        }); 

    }
    else{
        var quitarLike = db.collection("posteos").doc(`${item.id}`);

        return quitarLike.update({
            likes : firebase.firestore.FieldValue.arrayRemove(`${auth.currentUser.email}`)
        })
        .then(() => {
            this.setState({
            /*  likes: this.state.likes-=1, */
                liked: false
            })
        })
        .catch((error) =>{
            console.error("Error updating document: ", error);
        }); 

    }
}





    render(){
        console.log(this.props.data.item.data)
        let {data} = this.props.data.item
  return (
    <View style={styles.container}>

      <Text>usuario: {data.user}</Text>
      <Text>titulo: {data.title}</Text>
      <Text>contenido: {data.description}</Text>
      <Text>likes:{data.likes.length}</Text>
      <TouchableOpacity onPress={()=>this.like()}>
                    <Text>Likear</Text>

                </TouchableOpacity>





    </View>
  );
    }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});

export default Fotos
