import React,{Component} from 'react';
import { StyleSheet, Text, View, TouchableOpacity } from 'react-native';
import { auth, db } from '../firebase/config';
import Fotos from '../components/paginafotos';


class Perfil extends Component{
    constructor(props){
      super(props)
      this.state={
        posts:[],
        deletePost: ''
      }
    }
    componentDidMount() {
      //Traer datos de la db
      db.collection('posts').orderBy('createdAt', 'desc').where("user", "==", this.props.currentUser.email).onSnapshot(
          docs => {
              let posteos = [];
              docs.forEach( doc => {
                  posteos.push({
                      id: doc.id,
                      data: doc.data()
                  })
              })

              this.setState ({
                  posts: posteos
              })
          }
      )

      }
    render(){
  return (
    <View style={styles.container}>
      <Text>Pagina del Perfil!!</Text>
      <Text>Email del usuario: {auth.currentUser.email}</Text>
      <Text>Fecha de Creacion: {}</Text>
      <Text>Ultima Conexion: {}</Text>
      <TouchableOpacity  onPress={()=> this.props.logout()}>
                <Text>Desloguearse</Text>
            </TouchableOpacity>
            <Text> Publicaciones </Text>
                <FlatList
                    data = {this.state.posts}
                    keyExtractor = {post => post.id}
                    renderItem = {({item}) => (<Fotos data={item.data} id={item.id} />) } >
                </FlatList>     


    </View>
  );
    }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});

export default Perfil
